window.onload=function(){var start=new Date("2024/5/12 00:00:00").getTime();function size(d){return d<10?"0"+d:d}setInterval((function(){var now=(new Date).getTime(),run,sumSeconds=parseInt((now-start)/1e3),d=parseInt(sumSeconds/86400),h=parseInt(sumSeconds%86400/3600),min=size(parseInt(sumSeconds/60%60)),m=size(parseInt(sumSeconds%60));document.querySelector("#runtime").innerHTML="花期: "+d+" 天 "+h+" 小时 "+min+" 分 "+m+" 秒"}),1e3)};